        <section class="" style=" text-align: center; margin-top: 10px; margin-bottom: 10px;  box-sizing: border-box; "><img src="<?php echo $filePath;?>fing38_5.jpg" style="display: block; margin:0 auto; max-width:500px;"/></section>
